package me.deveon.quick_ideas.main.events;import static me.deveon.quick_ideas.main.utils.Other.sortBuffers;import java.util.ArrayList;import java.util.Arrays;import java.util.List;import org.bukkit.event.EventHandler;import org.bukkit.event.Listener;import org.bukkit.event.server.TabCompleteEvent;public class CommmandTabCompletion implements Listener {  @EventHandler  public void onTabComplete(TabCompleteEvent e) {    e.getSender().sendMessage(e.getBuffer().length() + "");    if (e.getCompletions().contains("/timer")        || e.getCompletions().contains("/deveon") || e.getCompletions().contains("/yt")){      if (!e.getSender().isOp()) {        List<String> completions = e.getCompletions();        completions.remove("/timer");        completions.remove("/deveon");        completions.remove("/yt");        e.setCompletions(completions);      }    }    if (e.getBuffer().length() >= 7) {      try {        if (e.getBuffer().substring(0, 7).equalsIgnoreCase("/Timer ")) {          ArrayList<String> typeCompletions = new ArrayList<>(Arrays.asList("Start", "Stop", "reset", "restart"));          for (int i = 7; i < 14; i++) {            if (sortBuffers(typeCompletions).contains(e.getBuffer().substring(i))) {              e.setCompletions(typeCompletions);            }          }        }else if (e.getBuffer().substring(0, 8).equalsIgnoreCase("/deveon ") || e.getBuffer().substring(0, 4).equalsIgnoreCase("/yt ")) {          ArrayList<String> typeCompletions = new ArrayList<>(Arrays.asList("wheelOfPrizes", "sharedDamage", "randomPlacedBlock"));          for (int i = 4; i < 23; i++) {            if (sortBuffers(typeCompletions).contains(e.getBuffer().substring(i))) {              e.setCompletions(typeCompletions);            }          }        }      } catch (StringIndexOutOfBoundsException ignore) {      }    }  }}