package me.deveon.quick_ideas.main.events;import me.deveon.quick_ideas.main.Main;import me.deveon.quick_ideas.main.scoreBoard.BaseScoreBoard;import org.bukkit.Bukkit;import org.bukkit.entity.Player;import org.bukkit.event.EventHandler;import org.bukkit.event.Listener;import org.bukkit.event.player.PlayerJoinEvent;import org.bukkit.scoreboard.Score;import org.bukkit.scoreboard.Scoreboard;public class onJoin implements Listener {  private Main instance;  public onJoin(Main main) {    instance = main;  }  @EventHandler  public void onPlayerJoin(PlayerJoinEvent e) {    Player p = e.getPlayer();    p.setScoreboard(BaseScoreBoard.getScoreBoard());    BaseScoreBoard.addScoreBoardViewer(p.getUniqueId());    if(Bukkit.getOnlinePlayers().size()>=1) {      BaseScoreBoard.updateScoreBoard();    }  }}