package me.deveon.quick_ideas.main.events;import me.deveon.quick_ideas.main.Main;import me.deveon.quick_ideas.main.scoreBoard.BaseScoreBoard;import org.bukkit.Bukkit;import org.bukkit.entity.Player;import org.bukkit.event.EventHandler;import org.bukkit.event.Listener;import org.bukkit.event.player.PlayerJoinEvent;import org.bukkit.event.player.PlayerQuitEvent;public class onQuit implements Listener {  private Main instance;  public onQuit(Main main) {    instance = main;  }  @EventHandler  public void onPlayerQuit(PlayerQuitEvent e) {    Player p = e.getPlayer();    BaseScoreBoard.removeScoreBoardViewer(p.getUniqueId());  }}