package me.deveon.quick_ideas.main.utils;import java.util.ArrayList;public class Other {  public static ArrayList<String> sortBuffers(ArrayList<String> completions) {    ArrayList<String> bufferSet = new ArrayList<>();    if (completions != null) {      bufferSet.add("");      for (String c : completions) {        for (int i = c.length(); i > 0; i--) {          if (!bufferSet.contains(c.substring(0, i))) {            if (i == 1) {              bufferSet.add(c.substring(0, i).toUpperCase());              bufferSet.add(c.substring(0, i).toLowerCase());            } else {              bufferSet.add(c.substring(0, i));            }          }        }      }    }    return bufferSet;  }}