package me.deveon.quick_ideas.random_placed_block.events;import me.deveon.quick_ideas.main.Main;import me.deveon.quick_ideas.main.utils.Math_Utils;import org.bukkit.Material;import org.bukkit.block.Block;import org.bukkit.entity.Player;import org.bukkit.event.EventHandler;import org.bukkit.event.Listener;import org.bukkit.event.block.BlockPlaceEvent;import java.util.ArrayList;import java.util.Arrays;public class onPlayerPlaceBlock implements Listener {    private Main instance;    public onPlayerPlaceBlock(Main main) {        instance = main;    }    @EventHandler    public void onBlockPlace(BlockPlaceEvent e) {        Player p = (Player) e.getPlayer();        Block placedBlock = e.getBlock();        ArrayList<Material> blocks = new ArrayList<Material>(Arrays.asList(Material.values()));        int randomBlock = Math_Utils.getRandomInt(instance, blocks.size(), 1);        try {            placedBlock.setType(blocks.get(randomBlock));        } catch (IllegalArgumentException iae) {            placedBlock.setType(Material.GRASS_BLOCK);        }    }}