package me.deveon.quick_ideas.shared_damage.events;import me.deveon.quick_ideas.main.Main;import org.bukkit.Bukkit;import org.bukkit.entity.EntityType;import org.bukkit.entity.Player;import org.bukkit.event.EventHandler;import org.bukkit.event.Listener;import org.bukkit.event.entity.EntityDamageByEntityEvent;import org.bukkit.event.entity.EntityDamageEvent;import org.bukkit.event.entity.EntityDamageEvent.DamageCause;public class onPlayerDamged implements Listener {  private Main instance;  public onPlayerDamged(Main main) {    instance = main;  }  @EventHandler  public void onPlayerDamgedByEntity(EntityDamageByEntityEvent e) {    if (e.getEntityType() == EntityType.PLAYER) {      if (e.getCause() != DamageCause.CUSTOM) {        Player p = (Player) e.getEntity();        for (Player allp : Bukkit.getOnlinePlayers()) {          if (allp.getUniqueId() != p.getUniqueId()) {            try{            allp.setHealth(allp.getHealth()-e.getDamage());            }catch (IllegalArgumentException ignore){allp.setHealth(0);}          }        }      }    }  }  @EventHandler  public void onPlayerDamgedEvent(EntityDamageEvent e) {    if (e.getEntity().getType() == EntityType.PLAYER) {      if (e.getCause() != DamageCause.CUSTOM) {        Player p = (Player) e.getEntity();        for (Player allp : Bukkit.getOnlinePlayers()) {          if (allp.getUniqueId() != p.getUniqueId()) {            try {              allp.setHealth(allp.getHealth() - e.getDamage());            }catch (IllegalArgumentException ignore){allp.setHealth(0);}          }        }      }    }  }}