package me.deveon.quick_ideas.wheel_of_prizes.cmds.admin.subcmds;import me.deveon.quick_ideas.main.utils.Messages_Templates;import me.deveon.quick_ideas.wheel_of_prizes.main.WOPMain;import org.bukkit.ChatColor;import org.bukkit.Sound;import org.bukkit.command.Command;import org.bukkit.command.CommandExecutor;import org.bukkit.command.CommandSender;import org.bukkit.command.ConsoleCommandSender;import org.bukkit.entity.Player;import org.bukkit.plugin.Plugin;public class stopSubCommand implements CommandExecutor {  private Plugin instance;  public stopSubCommand(Plugin plugin) {    instance = plugin;  }  public boolean onCommand(CommandSender commandSender, Command command, String s, String[] strings) {    if ((commandSender instanceof ConsoleCommandSender)) {      commandSender.sendMessage("Command can only be executed by a player");      return false;    }    Player player = (Player) commandSender;    if ((!commandSender.hasPermission("deveon_w.wopa.*"))) {      player.sendMessage("No perm");      return false;    }    if (strings.length == 1) {      if(WOPMain.getWheelOfPrizes().stopAll()){        Messages_Templates.customMessage(player, "" + ChatColor.DARK_RED + ChatColor.BOLD + "Stopped Wheel Event!", "", ChatColor.YELLOW,            ChatColor.DARK_GRAY);        player.playSound(player.getLocation(), Sound.BLOCK_NOTE_BLOCK_PLING,1,1);      }else{        Messages_Templates.customMessage(player, "" + ChatColor.RED + ChatColor.BOLD + "No Wheel Participates!", "", ChatColor.YELLOW,            ChatColor.DARK_GRAY);        player.playSound(player.getLocation(), Sound.BLOCK_NOTE_BLOCK_BASS,1,1);      }      return true;    }    Messages_Templates.usage(player, ChatColor.YELLOW, ChatColor.DARK_GRAY, "/WOPA Stop");    return false;  }}